     function play(index) //播放第index张图片
        {
            //每次播放时的任务，切换大图，bim+index的display:block, 更改小图 sim+index的margin-top:0px
            for (var i = 1; i <= 5; i++) {
                var bimg = document.getElementById("bim" + i);
                if (i == index) {
                    bimg.className = "imgshow";
                }
                else
                    bimg.className = "imghidden";
            }

            for (var i = 1; i <= 5; i++) {
                var simg = document.getElementById("sim" + i);
                if (i == index) {
                    simg.style.marginTop = "0px";
                }
                else
                    simg.style.marginTop = "15px";
            }
            //准备播放下一幅图片
            ++index;
            if (index > 5)
                index = 1;
            setTimeout("play("+index+")", 2000);
        }